stages:
stage("Upload Images to Docker Hub"){
            when {
                anyOf{
                    expression { return params.FORCE_DOCKERUPLOAD }
                    expression {
                        bbcShouldUploadArtifacts(branches: ["main"])
                    }
                }
            }
            steps{
                withBBCDockerhubRegistry {
                    bbcMake("push")
                }
            }
            post {
                always {
                    bbcGithubNotify(context: "push", status: env.result)
                }
            }
        }
